sections.css:
@import "base/body"
@import "base/blockquote"
@import "base/form"
@import "base/headings"
@import "base/link"
@import "base/list"
@import "base/paragraph"
@import "base/pre"
@import "base/table"

@import "components/accordion"
@import "components/badge"
@import "components/block"
@import "components/btn"
@import "components/cloudlet"
@import "components/donut-chart"
@import "components/dropdown"
@import "components/featured"
@import "components/field"
@import "components/icon"
@import "components/label"
@import "components/listing-item"
@import "components/live"
@import "components/logo"
@import "components/modal"
@import "components/nav-horizontal"
@import "components/nav-subway"
@import "components/nav-vertical"
@import "components/ornament"
@import "components/owl-carousel"
@import "components/page-title"
@import "components/pagination"
@import "components/parallax"
@import "components/popover"
@import "components/progress-bar"
@import "components/rating"
@import "components/ribbon"
@import "components/slogan"
@import "components/social-links"
@import "components/tabs"
@import "components/timeline"
@import "components/tooltip"
// New components

// Block
// --------------------------------------------------
// .field


// Elements
// --------------------------------------------------
.field__label
  font-family: $font-family-sans-condensed
  font-size: $font-size-small
  font-weight: $font-weight
  text-transform: uppercase
  color: $gray-light

.field__items
  font-weight: $font-weight-bold

// .field__item


// Modifiers
// --------------------------------------------------
.field--label-inline
  +clearfix()
  .field__label,
  .field__items
    display: inline-block
    float: left
  .field__label
    margin: 2px 5px 0 0


.field--margin
  margin-bottom: 20px

// Block
// --------------------------------------------------
.ornament:before
  content: ''
  display: block
  width: 30px
  height: 4px
  margin-bottom: 30px
  background: $brand-primary

// Modifiers
// --------------------------------------------------
.ornament--green:before
  background-color: $green

// Block
// --------------------------------------------------
.page-title
  text-transform: uppercase
  margin-bottom: 60px

@import "layout/layout--header"
@import "layout/layout--landing"
@import "layout/layout--content"
@import "layout/layout--footer"
// New layouts

// Designs overlay
@import "designs"

