sections.css:
@import "base/body"
@import "base/form"
@import "base/helper_classes"
@import "base/responsive-utilities"
@import "base/blockquote"
@import "base/headings"
@import "base/link"
@import "base/list"
@import "base/label"
@import "base/paragraph"

@import "bootstrap/alert"
@import "bootstrap/buttons"
@import "bootstrap/breadcrumb"
@import "bootstrap/carousel"
@import "bootstrap/label"
@import "bootstrap/modal"
@import "bootstrap/nav-tabs"
@import "bootstrap/nav-pills"
@import "bootstrap/pagination"
@import "bootstrap/panel"
@import "bootstrap/table"

// Component
// --------------------------------------------------
.label
  padding: 4px 10px !important
  // font-size: $font-size-base;
  font-weight: $font-weight
  display: inline-block

.label-default
  background-color: $azure

@import "layouts/layout"
@import "layouts/layout--1"
@import "layouts/layout--2"
@import "layouts/layout--3"
@import "layouts/layout--4"
@import "layouts/layout--5"
@import "layouts/layout--6"
@import "layouts/layout--7"

// Sections
@import "layouts/sections/section--footer"

@import "components/block"
@import "components/bookmark"
@import "components/box"
@import "components/counter"
@import "components/featured"
@import "components/field"
@import "components/file"
@import "components/icon"
@import "components/input-inner-addon"
@import "components/listing--blog"
@import "components/listing--grid"
@import "components/listing--middlealign"
@import "components/listing--numbered"
@import "components/listing--stream"
@import "components/listing"
@import "components/logo"
@import "components/middle-align"
@import "components/nav-horizontal"
@import "components/nav-sectiontitles"
@import "components/nav-subway"
@import "components/nav-tiles"
@import "components/nav-vertical"
@import "components/news-carousel"
@import "components/notification-counter"
@import "components/ornament"
@import "components/page-title"
@import "components/slogan"
@import "components/social-links"
@import "components/title-more"

// Component
// --------------------------------------------------
.file
  padding-left: 30px
  position: relative
  display: block

// Dependencies
// --------------------------------------------------
.file .icon
  position: absolute
  font-size: 50px
  top: -5px
  left: -15px

// Component
// --------------------------------------------------
.listing--blog {
  .listing__item-media {
    max-width: none;
    width: 100%;
    img {
      width: 100%;
      height: auto
    }
  }
  .listing__item-text {
    padding: 20px;
    background-color: $gray-lighter;
    .listing__item-title {
      margin: 0;
      height: $line-height-computed * 1.5;
    }
  }
}

// Component
// --------------------------------------------------
.listing--middlealign {
  font-size: $font-size-small;
  .listing__item {
    padding: 20px 0;
    width: 100%;
    @include middle-align();
    &:first-child {
      padding-top: 0;
    }

    .listing__item-row {
      // Row settings inherit from mixin
    }
    .listing__item-col {
      // Col settings inherit from mixin
      &:nth-of-type(2) {
        min-width: 25%;
      }
      &:nth-of-type(3) {
        min-width: 20%;
      }
      &:nth-of-type(4) {
        min-width: 10%;
      }
    }
  }
}

// Dependencies
// --------------------------------------------------
.listing--middlealign .icon {
  font-size: $font-size-h1;
  top: $font-size-base;
}

// Component
// --------------------------------------------------
.listing--numbered {
  counter-reset: listing--numbered;
  .listing__item {
    @include arrow($gray-light, #fff, 'left', 'in', 10px, 50px);
    margin: 35px 0 15px 80px;
    padding-top: 0;
    position: relative;
    overflow: visible;
  }
  .listing__item-text {
    padding-left: 55px;
    margin-bottom: $line-height-computed;
    &:before {
      position: absolute;
      left: -80px;
      top: 50%;
      width: 80px;
      height: 60px;
      margin: -30px 0;
      text-align: center;
      font-size: $font-size-h1 * 4/5;
    	content: counter(listing--numbered);
    	counter-increment: listing--numbered;
    }
    p:not(:last-child) {
      margin-bottom: $line-height-computed / 2;
    }
  }
  .listing__item-title {
    margin-bottom: $line-height-computed / 2;
    font-size: $font-size-h3 + 3;
  }
  .listing__item-footer {
    background-color: $gray-lightest;
    padding: 0 10px 0 35px;
    a, button {
      color: $brand-primary;
    }
  }
}


// Modifiers
// --------------------------------------------------
.listing--numbered.listing--condensed {
  .listing__item {
    @include arrow($gray-light, #fff, 'left', 'in', 5px, 10px);
    padding: 5px 10px 5px 0;
    margin: 5px 0 5px 30px;
  }
  .listing__item-text {
    padding-left: 30px;
    margin-bottom: 0;
    &:before {
      left: -30px;
      width: 30px;
      height: 20px;
      margin: -20px 0;
      font-size: $font-size-large;
      text-align: left;
    }
  }
}

// Component
// --------------------------------------------------
.listing--stream {
  .listing__item-text {
  }
  .listing__item-title {
    margin: 0;
    a {
      color: $link-color;
    }
  }
  .listing__item-footer {
    font-size: $font-size-small;
    line-height: $line-height-computed * 0.75;
  }
}

// Component
// --------------------------------------------------
.title-more
  width: 100%
  +middle-align
  @extend h2

// Elements
// --------------------------------------------------
.title-more__row
  // Row settings inherit from mixin

.title-more__col
  // Col settings inherit from mixin
  vertical-align: bottom
  .btn
    font-family: $font-family-base

// Dependencies
// --------------------------------------------------
.title-more
  h1, h2, h3, h4, h5, h6
    padding-bottom: 0

// Component
// --------------------------------------------------
.ornament
  &:before
    content: ''
    display: block
    width: 30px
    height: 4px
    margin-bottom: 30px
    background: $ornament-default

// Modifiers
// --------------------------------------------------
.ornament--azure:before
  background-color: $azure

// Dependencies
// --------------------------------------------------
.ornament.page-title
  margin-top: 0

// Component
// --------------------------------------------------
.page-title

// Modifiers
// --------------------------------------------------
.page-title--no-underline
  border-bottom: none
  margin-bottom: 0

.page-title--condensed
  padding-bottom: 5px

// Component
// --------------------------------------------------
.social-links
  line-height: $line-height-computed * 0.65
  text-align: center
  margin-left: 35px

// Elements
// --------------------------------------------------
.social-links__item
  line-height: $social-links-size * 2 / 3
  text-align: center

// Dependencies
// --------------------------------------------------
.social-links .icon
  +circle(transparent, 1px, $social-links-color)
  +icon-size($social-links-size * 2 / 3)
  color: $social-links-color
  width: $social-links-size
  height: $social-links-size
  line-height: $social-links-size

// Component
// --------------------------------------------------
.listing {
  margin-bottom: $line-height-computed;
}


// Elements
// --------------------------------------------------
.listing__label {
  margin-bottom: 0;
}

.listing__item,
.listing__pagination {
  overflow: hidden;
  padding-top: 45px;
  p {
    margin: 0;
    text-align: justify;
  }
}

.listing__item-media {
  float: left;
  margin-right: 30px;
  max-width: $listing-media-width-phone;
  // max-height: $listing-media-height-phone;
  overflow: hidden;

  .listing__item-media-icon {
    width: 40px;
    height: 40px;
    background-color: $gray-lightest;
    text-align: center;
    .icon {
      @include icon-size(40px);
      margin-top: -6px;
    }
  }
}

.listing__item-text {
  overflow: hidden;
}

.listing__item-title {
  margin-top: 0;
  a {
    color: $gray-base;
  }
  h2, h3 {
    margin: 0 !important;
  }
}

.listing__item-meta {
  font-size: $font-size-small;
  color: $gray-light;
  width: 100%;
  margin-bottom: $line-height-computed / 3;
}

.listing__item-footer {
  margin-top: $line-height-computed / 2;
  overflow: hidden;
}

.listing__icon {
  color: $brand-primary;
  font-size: $font-size-large;
  position: relative;
  top: 2px;
}

.listing__header,
.listing__filters,
.listing__footer,
.listing__pagination {
  width: 100%;
}

.listing__filters {
  background-color: $gray-lighter;
  padding: 5px 10px;
}

.listing__pagination {
  text-align: center;
}

.listing__header,
.listing__footer {
  padding-top: 30px;
}

// Modifiers
// --------------------------------------------------
.listing--2-col-layout {
  @include make-row(15px);
  .listing__label {
    @include make-md-column(12);
  }
  .listing__item {
    @include make-md-column(6);
  }
}

.listing--3-col-layout {
  @include make-row($grid-gutter-width);
  .listing__label {
    @include make-md-column($grid-columns);
  }
  .listing__item {
    @include make-md-column($grid-columns / 3);
  }
}

.listing--sm-phone-media-hidding {
  .listing__item-media {
    @extend .hidden-xxs;
  }
}

.listing--media-above {
  .listing__item-media {
    max-width: none;
    float: none;
    margin-right: 0;
    margin-bottom: 15px;
    img {
      width: 100%;
    }
  }
}

.listing--sm-phone-media-above {
  .listing__item-media {
    @media (max-width: $screen-xxs-max) {
      max-width: none;
      float: none;
      margin-right: 0;
      margin-bottom: 15px;
    }
  }
}

.listing--phone-media-above {
  .listing__item-media {
    @media (max-width: $screen-xs-max) {
      max-width: none;
      float: none;
      margin-right: 0;
      margin-bottom: 15px;
    }
  }
}

.listing--tablet-media-above {
  .listing__item-media {
    @media (min-width: $screen-sm-min) and (max-width: $screen-md-max) {
      max-width: none;
      float: none;
      margin-right: 0;
      margin-bottom: 20px;
      img {
        width: 100%;
      }
    }
  }
}

.listing--tabs-spacer {
  margin-top: -$line-height-computed * 1.666;
  .listing__label {
    margin-top: 0;
    padding-bottom: 0;
    border: none !important;
    overflow: hidden;
    &:before,
    &:after {
      content: none !important;
    }
    .btn {
      // z-index higher than in .tabs.
      position: relative;
      z-index: 2;
    }
  }
}

.listing--side-title {
  .listing__label,
  .title-more {
    border-bottom: none !important;
    &:before, &:after {
      content: none !important;
    }
  }
  @include breakpoint(tablet) {
    .listing__label,
    .title-more,
    .title-more__row,
    .title-more__col {
      display: block;
      padding: 0;
    }
    .title-more__row {
      @include arrow($gray-light, #fff, 'right', 'out', 25px, 30px);
      @include breakpoint(desktop) {
        margin-right: 25px;
      }
    }
    .title-more__col:first-child {
      margin-bottom: 20px;
    }
    .pull-right {
      float: none !important;
    }
  }
  &:not(.listing--2-col-layout):not(.listing--3-col-layout) {
    .listing__label,
    .title-more {
      @include make-sm-column(3);
      @include make-lg-column(2);
    }
    .listing__item {
      @include make-sm-column(9);
      @include make-lg-column(10);
      @include make-sm-column-offset(3);
      @include make-lg-column-offset(2);
    }
  }
  &.listing--2-col-layout {
    .listing__label,
    .title-more {
      @include make-sm-column(4);
      @include make-lg-column(2);
    }
    .listing__item {
      @include make-sm-column(4);
      @include make-lg-column(5);
      &:nth-of-type(2n+3) {
        @include make-sm-column-offset(4);
        @include make-lg-column-offset(2);
      }
    }
  }
  &.listing--3-col-layout {
    .listing__label,
    .title-more {
      @include make-sm-column(3);
    }
    .listing__item {
      @include make-sm-column(3);
      &:nth-of-type(3n+4) {
        @include make-sm-column-offset(3);
      }
    }
  }
}

// States
// --------------------------------------------------
@include breakpoint(desktop) {
  .listing__item-media {
    max-width: $listing-media-width-desktop;
    // max-height: $listing-media-height-desktop;
  }
}


// Dependencies
// --------------------------------------------------
.listing__label .btn {
  font-family: $font-family-sans-serif;
}

.listing--side-title {
  @include breakpoint(tablet) {
    .listing__label .btn {
      margin-top: 35px;
      float: none !important; // Reset all aligns
    }
  }
}


// Skins
// --------------------------------------------------
.listing--separators:not(.listing--2-col-layout) {
  .listing__item {
    border-bottom: 1px $gray-lighter solid;
    padding-bottom: 30px;

    &:last-of-type {
      border-bottom: none;
      padding-bottom: 0;
    }
  }
}
.listing--separators.listing--2-col-layout, {
  // In 2 col layout on small devices same as default.
  @media (max-width: $screen-sm-max) {
    .listing__item:not(:last-of-type) {
      border-bottom: 1px $gray-lighter solid;
      padding-bottom: 30px;
    }
  }

  // In 2 col layout on bigger devices change logic.
  @include breakpoint(tablet) {
    .listing__item:nth-of-type(odd):not(:nth-last-child(2)):not(:last-of-type),
    .listing__item:nth-of-type(even):not(:last-of-type)
     {
      border-bottom: 1px $gray-lighter solid;
      padding-bottom: 30px;
    }
  }
// TODO: Also for 3-col-layout
}

.listing--separators-dashed {
  .listing__item {
    border-bottom: 1px $gray-light dashed;
    padding-bottom: 30px;

    &:last-of-type {
      border-bottom: none;
      padding-bottom: 0;
    }
  }
}

.listing--condensed {
  .listing__item {
    padding-top: 15px;
    font-size: $font-size-small;
    h3.listing__item-title {
      font-size: $font-size-base;
    }
    h4.listing__item-title {
      font-size: $font-size-small;
    }
  }

  .listing__item-meta {
    font-family: $font-family-sans-condensed;
    font-weight: $headings-font-weight;
  }

  &.listing--separators,
  &.listing--separators-dashed {
    .listing__item {
      padding-bottom: 15px;

      &:last-child {
        padding-bottom: 0;
      }
    }
  }
}

.listing--condensed-lineheight {
  p {
    line-height: $line-height-computed - 4;
  }
}

.listing--zebra {
  .listing__item:nth-of-type(even) {
    background-color: $gray-lightest;
  }
}

.listing--image-corner-radius {
  .listing__item-media {
    border-bottom-left-radius: 30px;
  }
}
.listing--media-circle {
  .listing__item-media {
    @include circle();
  }
}

